ACC.validateForm.masking = function(el){
	var ph = el.val();
	if (ph.length == 10  ){
		ph = ph.replace(/^(\d{3})(\d{3})(\d{4,})+$/, "$1-$2-$3");
		el.val(ph);
		el.valid();
	}else if(ph.length < 12){
		var sanFormat = ph.replace(/-/g, '');
		el.val(sanFormat.length < 10 ? sanFormat : ph);
		if(sanFormat.length > 10 ){
			el.valid();
		}
	}
};
   	
