					$('.js-comment-container_'+id).find('td').find('.js-comment-textarea').attr('placeholder',placeholder);
					
					var updatedDateCol = $(this).find(".js-update-date");
					ACC.customerInquiry.displayLocalDate(updatedDateCol, "update");
					
