	mobileFormatValidation : function(ele, limit){
		ele.on("keyup",function (){
    		ACC.register.digitsValidationMethod($(this), limit);
    		
    	}).on("blur",function(){
    		var ph = $(this).val();
 			$(this).val(ph.replace(/^(\d{3})(\d{3})(\d{4,})+$/, "$1-$2-$3"));
    	}).on("focus",function(){
    		var ph = $(this).val();
 			$(this).val(ph.replace(/[-]/g, ""));
    	});
	}
